INSERT INTO `advantages` (`id`, `icon`, `icon_text`, `title`, `image`, `text`, `sort_id`, `active`, `created_at`, `updated_at`) VALUES
(1, 'advantages\\June2020\\NxUEWVsJtr4cHD9ZtJLD.svg', 'Профессионалы своего дела', 'Профессионалы своего дела', 'advantages/August2020/qA8gxNnB0uqPkMOOeJ9g.png', 'Профессионализм и надежность - главный принцип компании.\n\nКомпания успешно работает в Казахстане c 1999 года. За двадцать два года деятельности мы достигли уровня профессионализма, который в полной мере соответствует мировым стандартам.\nНакопленный опыт работы в разных сферах, мощный потенциал и используемые передовые технологии позволяют нашей компании успешно решать задачи любой сложности.', 1, 1, '2020-06-19 12:36:00', '2022-03-18 02:22:10'),
(2, 'advantages\\June2020\\34jyyj8yJuJtsanoATUm.svg', 'Официальный дистрибьютор', 'Официальный дистрибьютор', 'advantages/August2020/9BQELrxpQ16FDLyZMfUd.png', 'Компания является авторизированным дистрибьютором на территории Республики Казахстан ведущих компаний, таких как, Hytera, SAF Tehnika.\n\nВсе оборудование надежно и имеет долгий срок эксплуатации. Оно отличается высокой производительностью и емкостью, компактно и удобно в использовании, совместимо с остальными системами и средствами связи.', 2, 1, '2020-06-19 12:36:00', '2022-03-18 02:22:38'),
(3, 'advantages\\June2020\\IxQSCZWFBibeAADJNEy0.svg', 'Конфиденциальность', 'Конфиденциальность', 'advantages/August2020/vv071g8r6ABoJdhk6ZAY.png', 'В компании созданы все условия для работы с госсекретами, функционирует ПЗГС.\n\nЗа 22 года деятельности наша компания достигла высокого уровня профессионализма, полностью соответствующего мировым стандартам.\nРаботники компании имеют допуска к государственным секретам.', 3, 1, '2020-06-19 12:36:00', '2022-03-18 02:23:02'),
(4, 'advantages\\June2020\\pngUA3S303vHaLTmyOa7.svg', 'Сервисный центр', 'Сервисный центр', 'advantages/August2020/E5ZiDt6rtAS4x8VSCkuW.png', 'Наличие в компании собственного сервисного центра с годовым гарантийным и постгарантийным обслуживанием.\n\nНаши специалисты проходят курсы повышения квалификации и необходимое обучение в учебном центре «Hytera Communications Corporation Limited» дающее им право на проведение всех видов сервисных и ремонтных работ на оборудовании систем транкинговой радиосвязи цифрового стандарта DMR компании Hytera.\n', 4, 1, '2020-06-19 12:36:00', '2022-03-18 02:23:39'),
(5, 'advantages\\June2020\\obYJVIdPsERRof5tZX4U.svg', 'Собственное производство', 'Собственное производство', 'advantages/August2020/tLIOY8wNW9id4HZX6FiT.png', 'С течением времени появились свои производственные сборочные и ремонтные площади, благодаря чему к услугам компании добавилось собственное производство продукции (оборудование защиты средств информации).\n\nПродукция Компании внесена в «Реестр доверенной продукции электронной промышленности и программного обеспечения»\n', 5, 1, '2020-06-19 12:36:00', '2020-08-20 05:24:12');
