INSERT INTO `posts` (`id`, `author_id`, `title`, `seo_title`, `excerpt`, `body`, `body_footer`, `gallery`, `image`, `slug`, `meta_description`, `meta_keywords`, `status`, `featured`, `created_at`, `updated_at`, `date`) VALUES
(1, 1, 'Сбор-семинар «КазСпас»', '', 'Ежегодно в «Скальном городе Астана» расположенного на территории Алматинской области, спасательными подразделениями Комитета по чрезвычайным ситуациям Министерства внутренних дел Республики Казахстан проводится сбор-семинар «КазСпас». В текущем году данный сбор-семинар проводился 23 раз. ', '<p>Ежегодно в &laquo;Скальном городе Астана&raquo; расположенного на территории Алматинской области, спасательными подразделениями Комитета по чрезвычайным ситуациям Министерства внутренних дел Республики Казахстан проводится сбор-семинар &laquo;КазСпас&raquo;. В текущем году данный сбор-семинар проводился 23 раза.</p>\n<p>Компания Тополь, как непосредственные поставщики силовых структур приняли участие в выставке тактико-специальных учении и продемонстрировали оборудование производства Hytera.</p>\n<p>Специалисты показали как работает E-PACK 100, оборудование, предназначенное для быстрого и гибкого развертывания системы связи, может использоваться не только как радио для совершения и приема вызовов, но также может создавать беспроводную мобильную сеть для маршрутизации голоса.</p>\n<p>Smart Dispatch, которое интегрирует большинство общих функций необходимых для систем радиосвязи в одном простом решении, что не маловажно при экстренных случаях. Также были выставлены цифровые и аналоговые ретрансляторы, радиостанции разного вида.</p>', '', '[\"posts\\/July2020\\/FngFTzxQgAW977ccZnL6.jpg\"]', 'posts/July2020/cWTp4tuHdqtgyJfmJZ8f.jpg', 'sbor-seminar-kazspas', 'This is the meta description', 'keyword1, keyword2, keyword3', 'PUBLISHED', 0, '2020-06-16 12:07:11', '2020-07-14 14:37:04', '2018-10-02'),
(5, 1, 'Сергей Ерошенков дал интервью ИТ изданию «PROFIT»', '', 'В честь двадцатилетия компании основатель ТОО «Тополь» Сергей Ерошенков дал интервью ИТ изданию «PROFIT», в котором рассказал о развитии и достижениях товарищества за 20 лет своего существования на рынке системной интеграции. ', '<p>В честь двадцатилетия компании основатель ТОО &laquo;Тополь&raquo; Сергей Ерошенков дал интервью ИТ изданию &laquo;PROFIT&raquo;, в котором рассказал о развитии и достижениях товарищества за 20 лет своего существования на рынке системной интеграции. <br />По данной ссылке можно увидеть полное интервью: <a href=\"http://bit.ly/TOPOL-SEroshenkov\">http://bit.ly/TOPOL-SEroshenkov</a></p>', '', '[\"posts\\/July2020\\/h3z4xWkI9l8XMXNtx5ti.jpg\"]', 'posts/July2020/dj6R1pK5qT8aOxa7WU73.jpg', 'sergej-eroshenkov-dal-interv-yu-it-izdaniyu-profit', '', '', 'PUBLISHED', 0, '2020-07-10 08:32:12', '2020-08-18 08:24:03', '2020-08-18'),
(6, 1, 'Компания Тополь поздравляет всех причастных! ', '', 'Компания Тополь поздравляет всех причастных! \nВы с честью отстаиваете территориальную целостность и суверенитет Республики, надежно обеспечиваете безопасность и покой наших сограждан и мужественно рискуете своими жизнями. ', '<p>Компания Тополь поздравляет всех причастных! <br />Вы с честью отстаиваете территориальную целостность и суверенитет Республики, надежно обеспечиваете безопасность и покой наших сограждан и мужественно рискуете своими жизнями. <br />От всей души желаем здоровья и дальнейших успехов! Мира, добра и благополучия вам и вашим семьям!</p>', '', '[\"posts\\/July2020\\/VqdZvXEuEGMj4yEEQzh0.jpg\"]', 'posts/July2020/My2Gvk59w7L6UHwn2bEo.jpg', 'kompaniya-topol-pozdravlyaet-vseh-prichastnyh', '', '', 'PENDING', 0, '2020-07-10 08:35:49', '2020-07-24 13:19:18', '2019-11-05'),
(7, 1, 'Компания «Тополь» поздравляет с профессиональным праздником — днем работника статистики!', '', 'Желаем только лучших показателей, планомерных графиков, точных данных, отличных результатов! \nПусть Ваш труд будет благодарным, легким и показательным! Ваш труд и вклад — огромен!', '<p>Компания &laquo;Тополь&raquo; поздравляет с профессиональным праздником &mdash; днем работника статистики! Желаем только лучших показателей, планомерных графиков, точных данных, отличных результатов! <br />Пусть Ваш труд будет благодарным, легким и показательным! Ваш труд и вклад &mdash; огромен!</p>', '', '[\"posts\\/July2020\\/YMzPkWWROTvaYgNA75hU.jpg\"]', 'posts/July2020/ceTlasFMW6pj4qqDEnn2.jpg', 'kompaniya-topol-pozdravlyaet-s-professional-nym-prazdnikom-dnem-rabotnika-statistiki', '', '', 'PENDING', 0, '2020-07-10 08:37:39', '2020-07-24 13:20:16', '2019-11-08'),
(8, 1, 'День энергосбережения – 11 ноября ', '', '11 ноября по инициативе международной экологической сети «Школьный проект по использованию ресурсов и энергии» (SPARE) объявлено Днем энергосбережения. ', '<p>День энергосбережения &ndash; 11 ноября <br />История праздника. <br />11 ноября по инициативе международной экологической сети &laquo;Школьный проект по использованию ресурсов и энергии&raquo; (SPARE) объявлено Днем энергосбережения. <br />Решение об учереждении этого праздника было принято в апреле 2008 года на проходившем в Казахстане международном совещании координаторов SPARE. <br />А уже в ноябре 2008-го мир отметил первый День энергосбережения. Этот праздник получил статус международного, поскольку принять участие в проекте пожелали около 20 стран.</p>', '', '[\"posts\\/July2020\\/JnozrtFCZt79ok5ksnJf.jpg\"]', 'posts/July2020/cCKlUyiSfQR9YbtDl57B.jpg', 'den-energosberezheniya-11-noyabrya', '', '', 'PENDING', 0, '2020-07-10 08:39:05', '2020-07-24 13:20:40', '2019-11-11'),
(9, 1, 'Поздравляем вас с Днем национальной валюты- тенге', '', 'Поздравляем вас с Днем национальной валюты- тенге, с профессиональным праздником работников финансовой системы Республики Казахстан! \nЖелаем вам финансового благополучия, процветания, профессиональных достижений.', '<p>Дорогие партнеры и друзья! Поздравляем вас с Днем национальной валюты- тенге, с профессиональным праздником работников финансовой системы Республики Казахстан! <br />Желаем вам финансового благополучия, процветания, профессиональных достижений.</p>', '', '[\"posts\\/July2020\\/aQJRaS9VGaGm3iX9ETN3.jpg\"]', 'posts/July2020/bktClzZT7xrtPROYBe8Z.jpg', 'pozdravlyaem-vas-s-dnem-nacional-noj-valyuty-tenge', '', '', 'PENDING', 0, '2020-07-10 08:40:25', '2020-07-24 13:21:41', '2019-11-15'),
(10, 1, 'Поздравляем с Днём работника сельского хозяйства!', '', 'Желаем умело распоряжаться всеми ресурсами и непременно добиваться высоких результатов в деятельности.\n\nПусть надежды и ожидания каждого дня благополучно перерабатываются в успех и достаток!', '<p>Поздравляем с Днём работника сельского хозяйства!</p>\n<p>Желаем умело распоряжаться всеми ресурсами и непременно добиваться высоких результатов в деятельности.</p>\n<p>Пусть надежды и ожидания каждого дня благополучно перерабатываются в успех и достаток!</p>', '', '[\"posts\\/July2020\\/l7PvV9uqx2S1nagshFpy.jpg\"]', 'posts/July2020/6313calBTW3P30XpF3xY.jpg', 'pozdravlyaem-s-dnyom-rabotnika-sel-skogo-hozyajstva', '', '', 'PENDING', 0, '2020-07-10 08:41:31', '2020-07-24 13:22:05', '2019-11-17'),
(12, 1, 'День Первого Президента Республики Казахстан', '', 'Компания «Тополь» поздравляет вас с государственным праздником – Днем Первого Президента Республики Казахстан!', '<p>Уважаемые Казахстанцы!</p>\n<p>Компания &laquo;Тополь&raquo; поздравляет вас с государственным праздником &ndash; Днем Первого Президента Республики Казахстан!</p>\n<p>Благодаря Нурсултану Абишевичу Назарбаеву наша страна укрепила свою Независимость и прошла большой путь экономического, социального и культурного развития.</p>\n<p>Мы желаем Вам здоровья, достатка и новых достижений, а нашей стране &ndash; процветания и благополучия!</p>', '', '[\"posts\\/July2020\\/R3LoPkF4WbMIhDPPApyV.jpg\"]', 'posts/July2020/tYv4qVrPK5OQuIIvOD6c.jpg', 'den-pervogo-prezidenta-respubliki-kazahstan', '', '', 'PUBLISHED', 0, '2020-07-10 08:43:18', '2020-07-24 13:37:36', '2019-12-01'),
(13, 1, 'Компания «Тополь» поздравляет Вас с Днём работников Прокуратуры Республики Казахстан! ', '', 'Желаем в этот праздник быть твёрдыми в своих решениях, непоколебимыми — в своих принципах, \nнепобедимыми — в своей чести, неизменными — своему долгу.', '<p>Компания &laquo;Тополь&raquo; поздравляет Вас с Днём работников Прокуратуры Республики Казахстан! <br />Желаем в этот праздник быть твёрдыми в своих решениях, непоколебимыми &mdash; в своих принципах, <br />непобедимыми &mdash; в своей чести, неизменными &mdash; своему долгу. <br />Пусть деятельность будет беспрепятственна и успешна, пусть жизнь дарит яркие моменты счастья и добра!</p>', '', '[\"posts\\/July2020\\/Uls56Bq8wtb4xbUmwJA2.jpg\"]', 'posts/July2020/jriIkqmvuEIQ9vWjILYh.jpg', 'kompaniya-topol-pozdravlyaet-vas-s-dnyom-rabotnikov-prokuratury-respubliki-kazahstan', '', '', 'DRAFT', 0, '2020-07-10 08:44:28', '2020-07-24 13:38:03', '2019-12-06'),
(14, 1, 'Поздравляем с профессиональным праздником - С днём Энергетика!', '', 'И от всего сердца желаем спокойной и беспрепятственной работы, лёгких путей, решения любых вопросов и проблем, теплоты родных сердец и яркого света в душе!', '<p>Поздравляем с профессиональным праздником - С днём Энергетика!</p>\n<p>И от всего сердца желаем спокойной и беспрепятственной работы, лёгких путей, решения любых вопросов и проблем, теплоты родных сердец и яркого света в душе!</p>', '', '[\"posts\\/July2020\\/TTwZadvUaFauVs6Th7UN.jpg\"]', 'posts/July2020/gF6z6etyBPbl4uSSkWHA.jpg', 'pozdravlyaem-s-professional-nym-prazdnikom-s-dnyom-energetika', '', '', 'DRAFT', 0, '2020-07-10 08:45:36', '2020-07-24 13:38:20', '2019-12-15'),
(15, 1, 'Компания «Тополь» поздравляет с Днём Независимости в Казахстане!', '', 'Желаем мира и благополучия жителям Казахстана. Пускай год за годом наше государство становится все сильнее и богаче!', '<p>Компания &laquo;Тополь&raquo; поздравляет с Днём Независимости в Казахстане!</p>\n<p>Желаем мира и благополучия жителям Казахстана.</p>\n<p>Пускай год за годом наше государство становится все сильнее и богаче!</p>', '', '[\"posts\\/July2020\\/xpOWK2Yu3mvhoS3lJxIq.jpg\"]', 'posts/July2020/HyRh8YG9iw8O0QEYAYnt.jpg', 'kompaniya-topol-pozdravlyaet-s-dnyom-nezavisimosti-v-kazahstane', '', '', 'PUBLISHED', 0, '2020-07-10 08:46:33', '2020-07-10 08:46:33', '2019-12-16'),
(16, 1, 'День Государственной фельдъегерской службы', '', 'В этот особый день хотим пожелать Вам крепкого здоровья, счастья, семейного благополучия, а также мужества и отваги продолжать нести вашу службу!', '<p>Компания &laquo;Тополь&raquo; от всей души поздравляет с профессиональным праздником &ndash; Днем Государственной фельдъегерской службы Республики Казахстан!</p>\n<p>В этот особый день хотим пожелать Вам крепкого здоровья, счастья, семейного благополучия, а также мужества и отваги продолжать нести вашу службу!</p>', '', '[\"posts\\/July2020\\/2W3y3swYtL3ABKdJTwKY.jpg\"]', 'posts/July2020/vZ5TUo3XscN8O9vFJuWV.jpg', 'den-gosudarstvennoj-fel-d-egerskoj-sluzhby', '', '', 'DRAFT', 0, '2020-07-10 08:48:14', '2020-07-24 13:39:25', '2019-12-20'),
(17, 1, 'Компания «Тополь» поздравляет Вас с наступающим Новым годом! ', '', 'Пусть новый год будет для вас плодотворным и успешным. \nЖелаем каждому новых шагов вверх по карьерной лестнице.', '<p>Компания &laquo;Тополь&raquo; поздравляет Вас с наступающим Новым годом! <br />Пусть новый год будет для вас плодотворным и успешным. <br />Желаем каждому новых шагов вверх по карьерной лестнице. Легких вам трудовых будней и приятных отпусков! <br />Пусть все неудачи и невзгоды останутся за порогом старого года, а в новом ― будут только взлеты и достижения!</p>\n<p>С уважением, коллектив ТОО &laquo;Тополь&raquo;.</p>', '', '[\"posts\\/July2020\\/WTBUWH3WNBYRQBGdADUT.jpg\"]', 'posts/July2020/AKfiYIxMscJPNWYkt9nR.jpg', 'kompaniya-topol-pozdravlyaet-vas-s-nastupayushim-novym-godom', '', '', 'PUBLISHED', 0, '2020-07-10 08:49:55', '2020-07-10 08:49:55', '2019-12-27'),
(18, 1, 'В светлый праздник Рождества желаем вам добра и счастья', '', 'Пусть жизнь Ваша будет мирной, спокойной, счастливой.', '<p>В светлый праздник Рождества желаем вам добра и счастья, благополучия и уюта вашему дому, душевной теплоты, взаимопонимания и поддержки в семье. Пусть жизнь Ваша будет мирной, спокойной, счастливой.</p>', '', '[\"posts\\/July2020\\/juCN5mpIPEq32zfeieLM.jpg\"]', 'posts/July2020/1jlKDGQ3DpaCVNU4prF8.jpg', 'v-svetlyj-prazdnik-rozhdestva-zhelaem-vam-dobra-i-schast-ya', '', '', 'PUBLISHED', 0, '2020-07-10 08:51:02', '2020-07-10 08:51:02', '2020-01-07'),
(19, 1, 'Компания «Тополь» поздравляет с Днём Национальной гвардии Республики Казахстан', '', 'Компания «Тополь» поздравляет с Днём Национальной гвардии Республики Казахстан и от души желает честью души и отвагой сердца защищать интересы своего государства, желает ни на минуту не усомниться в важности своей службы и бравых дел, желает с гордостью и почётом нести знамя гвардии Республики Казахстан!', '<p>Компания &laquo;Тополь&raquo; поздравляет с Днём Национальной гвардии Республики Казахстан и от души желает честью души и отвагой сердца защищать интересы своего государства, желает ни на минуту не усомниться в важности своей службы и бравых дел, желает с гордостью и почётом нести знамя гвардии Республики Казахстан!</p>\n<p>&nbsp;</p>', '', '[\"posts\\/July2020\\/Ht7LMt5qtkCmgVPzzu1V.jpg\"]', 'posts/July2020/CaPOzkgQojCl8ug0DLNy.jpg', 'kompaniya-topol-pozdravlyaet-s-dnyom-nacional-noj-gvardii-respubliki-kazahstan', '', '', 'DRAFT', 0, '2020-07-10 08:52:32', '2020-07-24 13:39:54', '2020-01-10'),
(20, 1, 'Сегодня значимая дата — день вывода войск из Афганистана! ', '', 'Компания «Тополь» поздравляет всех, кто вернулся, кто честью и силой, трудом и отвагой стоял за идею, выполнял команды и действовал по приказу. ', '<p>Сегодня значимая дата &mdash; день вывода войск из Афганистана! <br />Компания &laquo;Тополь&raquo; поздравляет всех, кто вернулся, кто честью и силой, трудом и отвагой стоял за идею, выполнял команды и действовал по приказу. <br />Вечная память погибшим! <br />Желаем всем мирного неба над головой, крепкого здоровья и храбрости духа!</p>', '', '[\"posts\\/July2020\\/GNkJa0dkAmgSbJtDSTt4.jpg\"]', 'posts/July2020/cLu7oEtWCgCEtu06GuXK.jpg', 'segodnya-znachimaya-data-den-vyvoda-vojsk-iz-afganistana', '', '', 'DRAFT', 0, '2020-07-10 08:53:50', '2020-07-24 13:40:16', '2020-01-15'),
(21, 1, 'Компания «Тополь» поздравляет с Днём благодарности!', '', 'Цените всё, что есть в вашей жизни, с благодарностью принимайте каждый день, каждое тёплое слово, каждый счастливый момент!', '<p>Компания &laquo;Тополь&raquo; поздравляет с Днём благодарности!</p>\n<p>Цените всё, что есть в вашей жизни, с благодарностью принимайте каждый день, каждое тёплое слово, каждый счастливый момент!</p>', '', '[\"posts\\/July2020\\/BqkB1FmLAH3SWQH7hlDx.png\"]', 'posts/July2020/Xo2v7IJm6ypvmqKiz7hP.png', 'kompaniya-topol-pozdravlyaet-s-dnyom-blagodarnosti', '', '', 'DRAFT', 0, '2020-07-10 08:54:59', '2020-07-24 13:40:37', '2020-03-01'),
(22, 1, 'В этот праздничный день с удовольствием поздравляем очаровательных дам!', '', 'Желаем встретить весну улыбками, комплиментами и отличным настроением. Процветания вам, успехов, профессиональных достижений, личных побед, здоровья и достатка.', '<p>В этот праздничный день с удовольствием поздравляем очаровательных дам! Желаем встретить весну улыбками, комплиментами и отличным настроением.</p>\n<p>Процветания вам, успехов, профессиональных достижений, личных побед, здоровья и достатка.</p>', '', '[\"posts\\/July2020\\/KeePUAle5fKZAo65GUnQ.jpg\"]', 'posts/July2020/2pH2oJxFrzAfZAJzaBmL.jpg', 'v-etot-prazdnichnyj-den-s-udovol-stviem-pozdravlyaem-ocharovatel-nyh-dam', '', '', 'PUBLISHED', 0, '2020-07-10 08:56:01', '2020-07-10 08:56:01', '2020-03-06'),
(23, 1, 'Компания Тополь поздравляет с днем землеустройства, геодезии и картографии.', '', 'Хотим пожелать, чтобы Ваша профессия никогда не оставалась без внимания, чтобы все вложенные Вами силы и средства непременно были оправданы бравым делом, чтобы все карты существенно облегчали жизнь людям и деятельность многих отраслей.', '<p>Компания Тополь поздравляет с днем землеустройства, геодезии и картографии.</p>\n<p>Хотим пожелать, чтобы Ваша профессия никогда не оставалась без внимания, чтобы все вложенные Вами силы и средства непременно были оправданы бравым делом, чтобы все карты существенно облегчали жизнь людям и деятельность многих отраслей.</p>', '', '[\"posts\\/July2020\\/MqXcpAKODBCjiSN9Fivw.jpg\"]', 'posts/July2020/afSKeo4Y47AlMYdUVDwt.jpg', 'kompaniya-topol-pozdravlyaet-s-dnem-zemleustrojstva-geodezii-i-kartografii', '', '', 'DRAFT', 0, '2020-07-10 08:56:51', '2020-07-24 13:40:58', '2020-03-14'),
(24, 1, 'Компания «Тополь» поздравляет с Наурызом!', '', 'Пусть этот праздник принесет Вам много тепла, солнечных дней и приятных моментов. Желаем, чтоб все начинания оправдались, а мечты сбылись. Радости, позитива, счастья и процветания!', '<p>Компания &laquo;Тополь&raquo; поздравляет с Наурызом!</p>\n<p>Пусть этот праздник принесет Вам много тепла, солнечных дней и приятных моментов. Желаем, чтоб все начинания оправдались, а мечты сбылись. Радости, позитива, счастья и процветания!</p>', '', '[\"posts\\/July2020\\/uQrucz0LaQDoWtMVjLyD.png\"]', 'posts/July2020/1CN1903sAyvt5mBd4Yz1.png', 'kompaniya-topol-pozdravlyaet-s-nauryzom', '', '', 'PUBLISHED', 0, '2020-07-10 08:57:48', '2020-07-10 08:57:48', '2020-03-22'),
(25, 1, 'День противопожарной службы Республики Казахстан', '', 'Желаем Вам крепкого здоровья, волевых решений, смелых поступков. Пусть будет больше спокойных дней в Ваших трудовых буднях!', '<p>Коллектив компании &laquo;Тополь&raquo; поздравляет Вас с профессиональным праздником -Днем противопожарной службы Республики Казахстан!</p>\n<p>Желаем Вам крепкого здоровья, волевых решений, смелых поступков. Пусть будет больше спокойных дней в Ваших трудовых буднях!</p>', '', '[\"posts\\/July2020\\/p5Y2XGTt7RIlwKbQ0FQ4.jpg\",\"posts\\/July2020\\/M2PRkJtLz32mKczOVuh5.jpg\"]', 'posts/July2020/RYXYDce0Mh34mqZLEbHx.jpg', 'den-protivopozharnoj-sluzhby-respubliki-kazahstan', '', '', 'DRAFT', 0, '2020-07-10 08:58:55', '2020-07-24 13:41:25', '2020-03-17'),
(26, 1, 'Коллектив компании «Тополь» поздравляет Сотрудников Службы государственной охраны с профессиональным праздником! ', '', 'Желаем всего самого наилучшего и оставаться таким же стойкими, мужественными и смелыми, чтобы вами гордилась вся страна. ', '<p>Коллектив компании &laquo;Тополь&raquo; поздравляет Сотрудников Службы государственной охраны с профессиональным праздником! <br />Желаем всего самого наилучшего и оставаться таким же стойкими, мужественными и смелыми, чтобы вами гордилась вся страна. <br />Желаем покорять одну вершину за другой и достигать поставленных целей. Мира и благополучия вам и вашим близким!</p>', '', '[\"posts\\/July2020\\/LYA3Uw4bjaj7rLjciBuD.jpg\"]', 'posts/July2020/4bxyRAXKoHFyx9KBqnQQ.jpg', 'kollektiv-kompanii-topol-pozdravlyaet-sotrudnikov-sluzhby-gosudarstvennoj-ohrany-s-professional-nym-prazdnikom', '', '', 'DRAFT', 0, '2020-07-10 09:00:53', '2020-07-24 13:41:46', '2020-04-21'),
(27, 1, 'Коллектив компании «Тополь» поздравляет Вас с Днём народного единства!', '', 'Желаем жить в мире и дружбе народов! Чтобы терпимость, толерантность, взаимоуважение и поддержка были основой дружественного и мирного сожительства всех народов и народностей, проживающих в нашей Республике.', '<p>Коллектив компании &laquo;Тополь&raquo; поздравляет Вас с Днём народного единства! Желаем жить в мире и дружбе народов! Чтобы терпимость, толерантность, взаимоуважение и поддержка были основой дружественного и мирного сожительства всех народов и народностей, проживающих в нашей Республике.</p>\n<p>С праздником!</p>', '', '[\"posts\\/July2020\\/X3Pir2O3xXbwoHwrPcWi.jpg\"]', 'posts/July2020/VHo9ICvYkMjbecMBokhC.jpg', 'kollektiv-kompanii-topol-pozdravlyaet-vas-s-dnyom-narodnogo-edinstva', '', '', 'PUBLISHED', 0, '2020-07-10 09:01:56', '2020-07-10 09:01:56', '2020-05-01'),
(28, 1, 'Компания «Тополь» поздравляет с Днем защитника отечества', '', 'Компания «Тополь» поздравляет с Днем защитника отечества всех, кто не представляет своей жизни без ответственности за жизнь и спокойствие других, кому понятен смысл слов «честь» и «долг», кто интересы общественные ставит выше личных! ', '<p>Компания &laquo;Тополь&raquo; поздравляет с Днем защитника отечества всех, кто не представляет своей жизни без ответственности за жизнь и спокойствие других, кому понятен смысл слов &laquo;честь&raquo; и &laquo;долг&raquo;, кто интересы общественные ставит выше личных! Мира вам любви и счастья</p>', '', '[\"posts\\/July2020\\/ALzbZPv5EiDEHEBtxbbi.jpg\"]', 'posts/July2020/nZ3NpePfeBlES9bYVh2X.jpg', 'kompaniya-topol-pozdravlyaet-s-dnem-zashitnika-otechestva', '', '', 'PUBLISHED', 0, '2020-07-10 09:03:23', '2020-07-10 09:03:23', '2020-05-07'),
(29, 1, 'Компания «Тополь» поздравляет всех с 75 -летием Победы!', '', 'Хотим пожелать, чтобы этот отрезок истории никогда не повторялся, и все жили в мире и согласии. Ветеранам желаем всех благ и уважения, искренне благодарим за подвиг! ', '<p>Компания &laquo;Тополь&raquo; поздравляет всех с 75 -летием Победы!</p>\n<p>Этот день &mdash; день радости и скорби.</p>\n<p>Хотим пожелать, чтобы этот отрезок истории никогда не повторялся, и все жили в мире и согласии. Ветеранам желаем всех благ и уважения, искренне благодарим за подвиг! А павшим на войне &mdash; вечная память. С Днём Победы!</p>', '', '[\"posts\\/July2020\\/kdwuagRAPrJ3yyW4G6p6.jpg\"]', 'posts/July2020/vXpQHoNXcXEOnswl3VxG.jpg', 'kompaniya-topol-pozdravlyaet-vseh-s-75-letiem-pobedy', '', '', 'PUBLISHED', 0, '2020-07-10 09:04:21', '2020-07-10 09:04:21', '2020-05-09'),
(30, 1, 'С Днем работников связи и информатизации!', '', 'Желаем вам профессиональной непоколебимости, творческого вдохновения и отсутствия помех!', '<p>Уважаемые работники связи и информации!</p>\n<p>Компания &laquo;Тополь&raquo; от всей души поздравляем Вас с профессиональным праздником - Днем работников связи и информатизации!</p>\n<p>Желаем вам профессиональной непоколебимости, творческого вдохновения и отсутствия помех!</p>', '', '[\"posts\\/July2020\\/PXj6Cjw4RJUhfHoGWBph.jpg\"]', 'posts/July2020/h2FRdaRDkg4IfHJnLf3m.jpg', 's-dnem-rabotnikov-svyazi-i-informatizacii', '', '', 'PUBLISHED', 0, '2020-07-10 09:05:44', '2020-07-10 09:05:44', '2020-05-17'),
(31, 1, 'Уважаемые работники культуры и искусства, позвольте поздравить вас с вашим профессиональным праздником!', '', 'Желаем вам ярких проектов в работе, творческого вдохновения и потенциала, новых творческих успехов и неимоверной фантазии, а что самое главное удовольствия от своей работы.', '<p>Уважаемые работники культуры и искусства, позвольте поздравить вас с вашим профессиональным праздником!</p>\n<p>Желаем вам ярких проектов в работе, творческого вдохновения и потенциала, новых творческих успехов и неимоверной фантазии, а что самое главное удовольствия от своей работы.</p>\n<p>Счастья, успехов, здоровья. Почет и уважение всем вам!</p>', '', '[\"posts\\/July2020\\/IbLbyaPRoOzN8uuNI0A1.jpg\"]', 'posts/July2020/E0PASX4dXNtWrk4LMKFj.jpg', 'uvazhaemye-rabotniki-kul-tury-i-iskusstva-pozvol-te-pozdravit-vas-s-vashim-professional-nym-prazdnikom', '', '', 'DRAFT', 0, '2020-07-10 09:06:37', '2020-07-24 13:42:11', '2020-05-21'),
(32, 1, 'С днем работников химической промышленности!', '', 'Коллектив компании «Тополь» поздравляет с профессиональным праздником - Днем работников химической промышленности!', '<p>Коллектив компании &laquo;Тополь&raquo; поздравляет с профессиональным праздником - Днем работников химической промышленности!</p>\n<p>Желаем всем труженикам химической индустрии дальнейших успехов в реализации намеченных планов, крепкого здоровья, счастья и благополучия, а трудовым коллективам &ndash; стабильной работы и дальнейшего развития!</p>', '', '[\"posts\\/July2020\\/Nrp2bEBiMb6VGdIbvZcY.jpg\"]', 'posts/July2020/tnNL7a5Woeqz6HQusXw9.jpg', 's-dnem-rabotnikov-himicheskoj-promyshlennosti', '', '', 'DRAFT', 0, '2020-07-10 09:07:38', '2020-07-24 13:42:38', '2020-05-31'),
(33, 1, '4 июня - День государственных символов', '', 'Государственные символы имеют нравственное значение для всех граждан нашего государства. Флаг, Герб, Гимн – это не просто символы, они воплощают собой все ценности государства и мы должны с уважением и патриотизмом относиться к ним.', '<p>4 июня - День государственных символов.</p>\n<p>Государственные символы имеют нравственное значение для всех граждан нашего государства. Флаг, Герб, Гимн &ndash; это не просто символы, они воплощают собой все ценности государства и мы должны с уважением и патриотизмом относиться к ним.</p>\n<p>В этот праздничный день компания &laquo;Тополь&raquo; желает Вам благополучия, счастья, мира! Пусть процветает наш Казахстан!</p>', '', '[\"posts\\/July2020\\/eBqENgnFWdz7mCcOnnBM.jpg\"]', 'posts/July2020/QoixnA7Q0oOdSKojVEFQ.jpg', '4-iyunya-den-gosudarstvennyh-simvolov', '', '', 'PUBLISHED', 0, '2020-07-10 09:14:55', '2020-07-10 09:14:55', '2020-06-04'),
(34, 1, 'Есть такая профессия - защищать природу!', '', 'Поздравляем всех тех, кто прикладывает невероятное количество собственных сил, энергии и любви для того, чтобы сохранить мир таким, какой он есть сегодня!', '<p>Есть такая профессия - защищать природу!</p>\n<p>Поздравляем всех тех, кто прикладывает невероятное количество собственных сил, энергии и любви для того, чтобы сохранить мир таким, какой он есть сегодня!</p>\n<p>Будьте уверены в лучшем, настойчиво продвигайте свои идеи и добивайтесь потрясающих результатов.</p>\n<p>В ваших надежных руках вся планета!</p>', '', '[\"posts\\/July2020\\/0M9Ly8PHwvKrRAk3JbQu.jpg\"]', 'posts/July2020/8NETtF7Fu6AOxi0CjbQ2.jpg', 'est-takaya-professiya-zashishat-prirodu', '', '', 'DRAFT', 0, '2020-07-10 09:16:21', '2020-07-24 13:43:08', '2020-06-05'),
(35, 1, '2 июня в г. Жезказган ИП ЗЕРЕ проводило зарыбление Жездинского водохранилища.', '', 'Представитель нашей компании побывал на данном мероприятии и стал свидетелем запуска 500 000 малька карпа и сазана.', '<p>2 июня в г. Жезказган ИП ЗЕРЕ проводило зарыбление Жездинского водохранилища.</p>\n<p>Представитель нашей компании побывал на данном мероприятии и стал свидетелем запуска 500 000 малька карпа и сазана.</p>\n<p>Это очень важное мероприятие по восстановлению и сохранению популяции рыбы в водохранилище. И мы рады, что компания \"ТОПОЛЬ\" была приглашена на данное мероприятие.</p>\n<p>Со своей стороны специалисты нашей компании готовы оказать помощь в подборе и рекомендации необходимого оборудования для обеспечения качественной связи для егерей и инспекторов, обеспечивающих охрану окружающей среды.</p>', '', '[\"posts\\/July2020\\/i8yQesOlVjy8uJ9NrtD3.jpg\"]', 'posts/July2020/Bc53OJBJIVVQsxohwup0.jpg', '2-iyunya-v-g-zhezkazgan-ip-zere-provodilo-zaryblenie-zhezdinskogo-vodohranilisha', '', '', 'PUBLISHED', 0, '2020-07-10 09:18:41', '2020-07-10 09:18:41', '2020-06-06'),
(36, 1, 'Компания «Тополь» от всего сердца поздравляет всех медицинских работников с профессиональным праздником! ', '', 'С начала карантина все мы столкнулись с совершенно новой реальностью. И на Ваши плечи легло нелегкое бремя. Спасибо за ваш труд! Низкий вам поклон!', '<p>Компания &laquo;Тополь&raquo; от всего сердца поздравляет всех медицинских работников с профессиональным праздником! <br />С начала карантина все мы столкнулись с совершенно новой реальностью.</p>\n<p>И на Ваши плечи легло нелегкое бремя. Спасибо за ваш труд! Низкий вам поклон!</p>\n<p><br />Желаем Вам сил, терпения и выдержки, предусмотрительности, мудрости и профессионализма.</p>\n<p><br />Пусть каждый день ведёт к успеху и воплощению большой мечты.</p>\n<p><br />Будьте здоровы и помогайте оставаться такими другим!</p>', '', '[\"posts\\/July2020\\/EJEHOA3HDFXeXKP6YjC7.jpg\"]', 'posts/July2020/l0NKa25zRbELlrFJ0ntV.jpg', 'kompaniya-topol-ot-vsego-serdca-pozdravlyaet-vseh-medicinskih-rabotnikov-s-professional-nym-prazdnikom', '', '', 'PUBLISHED', 0, '2020-07-10 09:20:08', '2020-07-10 09:20:08', '2020-06-21'),
(37, 1, 'Компания Тополь поздравляет с Днём полиции и Днем государственного служащего!', '', 'Ваша работа требует большой ответственности, постоянного самоконтроля и выдержки.', '<p>Компания Тополь поздравляет с Днём полиции и Днем государственного служащего!</p>\n<p>Ваша работа требует большой ответственности, постоянного самоконтроля и выдержки.</p>\n<p>От души желаем всем крепкого здоровья, неиссякаемого энтузиазма, успехов, уважения и удачи!</p>', '', '[\"posts\\/July2020\\/eoClsJH6bVOQxY94cjtz.jpg\"]', 'posts/July2020/H0JvUzJ4V3PJHWbZJIYo.jpg', 'kompaniya-topol-pozdravlyaet-s-dnyom-policii-i-dnem-gosudarstvennogo-sluzhashego', '', '', 'DRAFT', 0, '2020-07-10 09:20:57', '2020-07-24 13:43:36', '2020-06-23'),
(38, 1, '28 июня - день работников средств массовой информации!', '', 'Компания «Тополь» желает вам вдохновения, успешной реализации новых идей и творческих проектов.', '<p>28 июня - день работников средств массовой информации!</p>\n<p>Компания &laquo;Тополь&raquo; желает вам вдохновения, успешной реализации новых идей и творческих проектов.</p>\n<p>Счастья и благополучия вам и вашим близким!</p>', '', '[\"posts\\/July2020\\/nJyhvRFwVTw2uI2tclfl.jpg\"]', 'posts/July2020/hOX1wmOrrsiNv8ZR0QPi.jpg', '28-iyunya-den-rabotnikov-sredstv-massovoj-informacii', '', '', 'DRAFT', 0, '2020-07-10 09:21:45', '2020-07-24 13:43:55', '2020-06-28'),
(39, 1, 'Компания «Тополь» поздравляет всех казахстанцев с праздником – Днем Столицы!', '', 'Желаем счастья, здоровья, благополучия! А нашей молодой столице желаем процветания, становиться с каждым годом все величественней и прекрасней, а также укреплять свои позиции в мировом сообществе.', '<p>Компания &laquo;Тополь&raquo; поздравляет всех казахстанцев с праздником &ndash; Днем Столицы!</p>\n<p>Желаем счастья, здоровья, благополучия! А нашей молодой столице желаем процветания, становиться с каждым годом все величественней и прекрасней, а также укреплять свои позиции в мировом сообществе.</p>', '', '[\"posts\\/July2020\\/ABrKyrOXKSJPTvuW1fY3.jpg\"]', 'posts/July2020/Y3a7KSQTuuvvYtDTT4RR.jpg', 'kompaniya-topol-pozdravlyaet-vseh-kazahstancev-s-prazdnikom-dnem-stolicy', '', '', 'PUBLISHED', 0, '2020-07-10 09:22:33', '2020-07-10 09:22:33', '2020-07-06'),
(40, 1, 'Компания «Тополь» поздравляет работников водного хозяйства', '', 'Желаем вам здоровья, сил, выдержки и вдохновения, чтобы и дальше дарить стране свои умения и опыт!', '<p>Компания &laquo;Тополь&raquo; благодарит в День работников водного хозяйства всех виновников торжества за компетентность, славный и профессиональный труд, благодаря которому вся страна может пользоваться водными богатствами!</p>\n<p>Желаем вам здоровья, сил, выдержки и вдохновения, чтобы и дальше дарить стране свои умения и опыт!</p>', '', '[\"posts\\/July2020\\/th1gHltX8mogmiIF4AzD.jpg\"]', 'posts/July2020/axj3Y3dVrJ9kcZba0m8c.jpg', 'kompaniya-topol-pozdravlyaet-rabotnikov-vodnogo-hozyajstva', '', '', 'DRAFT', 1, '2020-07-14 14:33:44', '2020-07-24 13:44:15', '2020-07-09'),
(42, 1, 'День сотрудников органов национальной безопасности Республики Казахстан', '', 'Компания «Тополь» поздравляет с профессиональным праздником и от всей души желаем службы без потерь, успехов в нелегком и ответственном труде, мира и благополучия!', '<p>13 июля в Казахстане свой профессиональный праздник отмечают сотрудники органов национальной безопасности Республики.</p>\n<p>Компания &laquo;Тополь&raquo; поздравляет с профессиональным праздником и от всей души желаем службы без потерь, успехов в нелегком и ответственном труде, мира и благополучия!</p>', '', '[\"posts\\/July2020\\/zl1yIWezjGtxZLsFk5zZ.jpg\"]', 'posts/July2020/6FOXyc9ee1WapJfaLDJH.jpg', 'den-sotrudnikov-organov-nacional-noj-bezopasnosti-respubliki-kazahstan', '', '', 'DRAFT', 0, '2020-07-14 14:36:18', '2020-07-24 13:44:37', '2020-07-13'),
(43, 1, 'Компания «Тополь» поздравляет с Днем Конституции Республики Казахстан!', '', 'Пусть в нашей стране всегда царят мир, стабильность и благополучие!', '<p>Уважаемые Казахстанцы!</p>\n<p>Компания &laquo;Тополь&raquo; поздравляет вас с государственным праздником &ndash; Днем Конституции Республики Казахстан!</p>\n<p>Пусть в нашей стране всегда царят мир, стабильность и благополучие!</p>', '', '[\"posts\\/August2020\\/bUI1GWg4MRvwF9YW7zbk.jpeg\"]', 'posts/August2020/jKm9KIbZ6xDwPzuuGZcd.jpeg', 'kompaniya-topol-pozdravlyaet-s-dnem-konstitucii-respubliki-kazahstan', '', '', 'PUBLISHED', 0, '2020-08-28 05:49:00', '2020-08-28 05:50:34', '2020-08-28'),
(44, 1, 'Подарок для Онкологического отделения в Центральной больнице города Жезказгана', '', 'Компания «Тополь» решила сделать подарок для Онкологического отделения в Центральной больнице города Жезказгана в виде комплекта мобильных радиостанций Hytera TF-515.', '<p class=\"MsoNormal\">12 ноября 2020 года прошла встреча представителей городской общественности с руководством городского Управления здравоохранения и КГП &laquo;Центральная больница города Жезказган&raquo;. Во встрече участвовал и представитель компании &laquo;Тополь&raquo; Фомин Валерий Олегович. На встрече были озвучены замечания и проблемы в области здравоохранения в городе Жезказган, а также планы и перспективы на ближайшее время.</p>\n<p class=\"MsoNormal\">Во время переговоров Главный врач Городской многопрофильной больницы Урал Сафиллович Мазитов поделился идеей по улучшению условий для пациентов &ndash; это организация связи между больными и медперсоналом, устройство прикроватных кнопок для вызова медицинских сестер и двухсторонней передачей голоса. Это значительно улучшит контроль за состоянием лежачих пациентов. Так же необходима связь между заведующими отделений и медицинскими сёстрами для оперативных консультаций.</p>\n<p class=\"MsoNormal\">Учитывая эти пожелания, Компания &laquo;Тополь&raquo; решила сделать подарок для Онкологического отделения в Центральной больнице города Жезказгана в виде комплекта мобильных радиостанций <span lang=\"EN-US\" style=\"mso-ansi-language: EN-US;\">Hytera</span> <span lang=\"EN-US\" style=\"mso-ansi-language: EN-US;\">TF</span>-515.</p>\n<p class=\"MsoNormal\">Радиостанции были вручены в присутствии медперсонала отделения человеку с золотыми руками, ежедневно спасающему жизни, заведующему отделением Врачу &ndash; онкогинекологу Жабаспаеву Болату Ишановичу.</p>\n<p class=\"MsoNormal\">Надеемся, что это сможет облегчить нелёгкий труд людей, ежедневно помогающим в трудную минуту.</p>', '', '[\"posts\\/November2020\\/8wTJ8NJCrGSTpQywaCS4.jpeg\",\"posts\\/November2020\\/ACJiUcql0QFCMI5S0xOK.jpeg\"]', 'posts/November2020/vk2S5b1XdOco9ODBoiji.jpeg', 'podarok-dlya-onkologicheskogo-otdeleniya-v-central-noj-bol-nice-goroda-zhezkazgana', '', '', 'PUBLISHED', 0, '2020-11-16 08:40:14', '2020-11-16 08:41:58', '2020-11-14'),
(45, 1, '1 декабря - День Первого Президента Республики Казахстан!', '', 'Компания «Тополь» поздравляет вас с праздником – Днем Первого Президента Республики Казахстан!', '<p style=\"margin: 0cm;\"><span style=\"font-size: 11.5pt; font-family: \'Arial\',sans-serif; color: #313131;\">Уважаемые Казахстанцы!</span></p>\n<p style=\"margin: 0cm;\"><span style=\"font-size: 11.5pt; font-family: \'Arial\',sans-serif; color: #313131;\">Компания &laquo;Тополь&raquo; поздравляет вас с праздником &ndash; Днем Первого Президента Республики Казахстан!</span></p>\n<p style=\"margin: 0cm;\"><span style=\"font-size: 11.5pt; font-family: \'Arial\',sans-serif; color: #313131;\">Благодаря Нурсултану Абишевичу Назарбаеву наша страна укрепила свою Независимость и прошла большой путь экономического, социального и культурного развития.</span></p>\n<p style=\"margin: 0cm;\"><span style=\"font-size: 11.5pt; font-family: \'Arial\',sans-serif; color: #313131;\">Мы желаем Вам здоровья, достатка и новых достижений, а нашей стране &ndash; процветания и благополучия!</span></p>', '', '[\"posts\\/December2020\\/1yOZSmTQi9uEWe1pRkLA.jpg\"]', 'posts/December2020/90im6C2dKqMaNEPwiYDM.jpg', '1-dekabrya-den-pervogo-prezidenta-respubliki-kazahstan', '', '', 'PUBLISHED', 0, '2020-12-04 06:41:41', '2020-12-04 06:41:41', '2020-12-01'),
(46, 1, '6 декабря - День работников Прокуратуры Республики Казахстан!', '', 'Компания «Тополь» поздравляет с Днём работников Прокуратуры Республики Казахстан!', '<p class=\"MsoNormal\" style=\"margin-bottom: 0cm; line-height: normal;\">Компания &laquo;Тополь&raquo; поздравля<span lang=\"KZ\" style=\"mso-ansi-language: KZ;\">ет </span>с Днём работников Прокуратуры <span lang=\"KZ\" style=\"mso-ansi-language: KZ;\">Республики Казахстан!</span></p>\n<p class=\"MsoNormal\" style=\"margin-bottom: 0cm; line-height: normal;\">Искренне жела<span lang=\"KZ\" style=\"mso-ansi-language: KZ;\">ем</span> всегда стоять на страже чести и справедливости в стране, никогда не терять профессиональной хватки и мастерства, уверенно решать любой вопрос и до конца доводить каждое дело.</p>\n<p class=\"MsoNormal\" style=\"margin-bottom: 0cm; line-height: normal;\">Также жела<span lang=\"KZ\" style=\"mso-ansi-language: KZ;\">ем</span> здоровья, терпения, уважения, удачи и успеха.</p>\n<p class=\"MsoNormal\" style=\"margin-bottom: 0cm; line-height: normal;\">&nbsp;</p>', '', '[\"posts\\/December2020\\/lgH8RY9FmWTcXEx4PP3h.png\"]', 'posts/December2020/gsH9X6Ha2e6DkX5P4B9P.png', '6-dekabrya-den-rabotnikov-prokuratury-respubliki-kazahstan', '', '', 'PUBLISHED', 0, '2020-12-04 08:02:59', '2020-12-04 08:02:59', '2020-12-06'),
(47, 1, 'День Независимости Республики Казахстан!', '', 'Компания «Тополь» поздравляет с Днём Независимости Республики Казахстан!', '<p class=\"MsoNormal\" style=\"margin-bottom: 0cm; line-height: normal;\">Компания &laquo;Тополь&raquo; поздравляет Вас с Днём Независимости Республики Казахстан!</p>\n<p class=\"MsoNormal\" style=\"margin-bottom: 0cm; line-height: normal;\">Этот день &ndash; символ свободы и уверенности нашей Родины!</p>\n<p class=\"MsoNormal\" style=\"margin-bottom: 0cm; line-height: normal;\">Желаем Вам и Вашим близким крепкого здоровья, счастья, уверенности в завтрашнем дне, мирного и светлого будущего.</p>', '', '[\"posts\\/December2020\\/huM5REEk7krfuH1bklfQ.png\"]', 'posts/December2020/0uHcJG6Pochic5yOthqN.png', 'den-nezavisimosti-respubliki-kazahstan', '', '', 'PUBLISHED', 0, '2020-12-15 12:03:27', '2020-12-15 12:04:22', '2020-12-16'),
(49, 1, 'Новый Год!', '', 'Компания «Тополь» поздравляет с наступающим 2021 годом!', '<p>Уважаемые клиенты и партнеры, дорогие наши друзья!</p>\n<p>В этот Новый год хочется от всей души поблагодарить вас за внимание к нам, интерес к тому, что мы делаем и, конечно, за ваше бесконечное доверие!</p>', '', '[\"posts\\/December2020\\/2yYWXPdwG1VdETqYcP7n.jpg\"]', 'posts/December2020/MyAHc56OnhDeaUCZhD5B.jpg', 'novyj-god', '', '', 'PUBLISHED', 0, '2020-12-28 12:52:44', '2020-12-28 12:54:44', '2020-12-28'),
(50, 1, '8 Марта!', '', 'Компания \"Тополь\" поздравляет всех женщин с прекрасным праздником весны - 8 Марта!', '<p>Компания \"Тополь\" поздравляет всех женщин с прекрасным праздником весны - 8 Марта!</p>\n<p>Пусть жизнь будет полна ярких идей и светлых перспектив, пусть в деятельности не будет препятствий для достижения успеха!</p>\n<p>Пусть ваша неотразимость и женственность, великолепие и невероятные силы позволяют успевать вам во всех делах, интересах и событиях!</p>', '', '[\"posts\\/March2021\\/gmBQdzY18fFIYBHgKFuZ.png\"]', 'posts/March2021/QBFFQ1oUlG8z8LrPUbDz.png', '8-marta', '', '', 'PUBLISHED', 0, '2021-03-05 05:37:08', '2021-03-05 05:38:15', '2021-03-05'),
